class Particle {
  constructor(x, y) {
    // createVector() makes a vector, which can store 2-3 values. in this case, it will store 2 values: x, y
    this.pos = createVector(x, y);
    this.vel = createVector(0, 0);
    // creates a p5 object that pulls random values
    this.acc = p5.Vector.random2D();
    // reduces magnitude
    this.acc.mult(0.05);
    // change beginning to end
    this.life = 255;
    // var for removing objects
    this.done = false;
    this.hueValue = 150;
    this.bright = 0;
  }

  update() {
    // Calling a method inside a class
    this.finished();

    // Vector objects can't be manipulated directly --> use .add()/.sub() function
    this.vel.add(this.acc);
    this.pos.add(this.vel);

    this.life -= 1;

    if(this.hueValue < 0) {
      this.hueValue = 255;
    }
    this.hueValue++;
  }

  display() {
    noStroke();
    fill(this.hueValue, 255, this.life);
    ellipse(this.pos.x, this.pos.y, 2);
  }

  finished() {
    if (this.life < 0) {
      this.done = true;
    } else {
      this.done = false;
    }
  }
}

class System {
  constructor(x, y) {
    this.x = x;
    this.y = y;

    this.particles = [];
    this.num = 100;

    this.done = false;

    // Use this.particles to push new Particle instances
    for (let i = 0; i < this.num; i++) {
      this.particles.push(new Particle(this.x, this.y));
    }
  }

  update() {
    this.finished();
    for (let i = this.particles.length - 1; i >= 0; i--) {
      this.particles[i].update();

      // Check if the Particle class has a `done` property or method
      if (this.particles[i].done) {
        this.particles.splice(i, 1);
      }
    }
  }

  display() {
    for (let i = 0; i < this.particles.length; i++) {
      this.particles[i].display();
    }
  }

  finished(){
    if (this.particles.length == 0){
      this.done = true;
    } else {
      this.done = false;
    }
  }
}

const serial = new p5.WebSerial();
let portButton;
let inData = [];

function serialEvent() {
  // this is called when data is recieved, data will then live in fromSerial
  var stringFromSerial = serial.readLine(); // reads everything till the new line charecter
  if (stringFromSerial) {
    // is there something there ?
    var trimmedString = trim(stringFromSerial); // get rid of all white space (returns, spaces, tabs)
    var myArray = split(trimmedString, ""); // splits the string into an array on commas

    for (i = 0; i < inData.length; i++);
    inData[i] = Number(myArray[i]);
    console.log(inData);
  }
}

function allSerialStuff() {
  if (!navigator.serial) {
