class Particle {
  constructor(x, y) {
    this.pos = createVector(x, y);
    this.vel = createVector(0, 0);
    this.acc = p5.Vector.random2D().mult(random(0.1, 0.5));
    this.life = 255; // Lifespan
    this.done = false;
    this.hueValue = 150; // Default hue if no input
  }

  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.life --; // Decrease lifespan
    this.done = this.life < 0; // Mark as done when lifespan ends
  }

  display() {
    noStroke();
    fill(this.hueValue, 255, this.life); // Use hue value with fade-out
    ellipse(this.pos.x, this.pos.y, 2); // Fixed size of 2
  }

//   finished() {
//     this.done = this.life < 0;
//   }
}

class System {
  constructor(x, y, velData = []) {
    this.pos = createVector(x, y);
    this.particles = [];
    this.velData = velData;
    this.done = false;
  }

  addParticle() {
    // Pass velData to each new particle
    this.particles.push(new Particle(this.pos.x, this.pos.y, this.velData));
  }

  update() {
    // Add new particles
    if (random(1) < 0.9) { // High probability for frequent particles
      this.addParticle();
    }

    // Update particles
    for (let i = this.particles.length - 1; i >= 0; i--) {
      this.particles[i].update();
      if (this.particles[i].done) {
        this.particles.splice(i, 1);
      }
    }

    // Check if all particles are done
    this.done = this.particles.length === 0;
  }

  display() {
    for (let particle of this.particles) {
      particle.display();
    }
  }
}

