// Exercise 9-8: Write a Button class (see Example 5-5 for a non-object-oriented button). The button
// class should register when a mouse is pressed over the button and change color.  Create button objects
// of different sizes and locations using an array. Before writing the main program, sketch out the
// Button class. Assume the button is off  when it first appears.


// Button class.
class Button {
  constructor(x, y, w, h, row, col, label) {
    this.x = x;
    this.y = y;
    this.w = w;
    this.h = h;
    this.row = row;
    this.col = col;
    this.bhol = label; // e.g., "tabla num" or "kartal"
    this.on = false;
  }

  click(mx, my) {
    if (
      mx > this.x &&
      mx < this.x + this.w &&
      my > this.y &&
      my < this.y + this.h
    ) {
      this.on = !this.on;
      if (this.on == true){ countBeats ++;}
      console.log(countBeats);
      if (this.on == false){ countBeats --;}
      console.log(countBeats);
    }
  }

  display() {
    stroke(10);
    fill(this.on ? color(0, 0, 140, 100) : color(220, 220, 220));
    rect(this.x, this.y, this.w, this.h);
  }
}

// Sound Class
class Sound {
  constructor(instrument, bhol, row) {
    this.instrument = instrument;
    this.bhol = bhol;
    this.x = 50;
    this.y = row * 40 + height / 2;
    this.size = 20;
  }

  display() {
    fill(0, 200, 100);
    rect(this.x, this.y, this.size, this.size);
  }

  play() {
    if (!loaded) {
      console.log("Waiting for Tone.js to load...");
      return;
    }
    // Check if the instrument supports the "has" method
    if (typeof this.instrument.has === "function") {
      if (this.instrument.has(this.bhol)) {
        this.instrument.player(this.bhol).start();
      } else {
        console.log("Sound", this.bhol, "not found in instrument");
      }
    } else {
      // Assume it's a Tone.Player; simply start it
      this.instrument.start();
    }
  }
}

