        sounds[fDhin].amp(1);

        sounds[pDhin].play();
        sounds[pDhin].amp(1);
      }
    }
  }

  // visualization
  noFill();
  stroke(255);
  ellipse((beat % 4) * 100 + 50, floor(beat / 4) * 100 + 50, 50, 50);
}

